INSERT INTO `books` (`id`,`isbn`,`title`,`subtitle`,`author`,`publisher`,`published_date`,`description`,`page_count`,`category`,`image_link`,`read`,`createdAt`,`updatedAt`) VALUES (83,'9781481432078','The Last Magician',NULL,'Lisa Maxwell','Simon and Schuster','2017-07-18','In an alternate version of present-day Manhattan, magic is dying, and Esta, a Mageus with a talent for manipulating time, travels back to 1901 to stop the destruction of a book that can possibly restore magic, but in Old New York, Esta must navigate gangs, secret societies, and her feelings for another magician in order to save the future of magic.','512','JUVENILE FICTION','http://books.google.com/books/content?id=kSgsDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api',0,'2020-03-09 00:48:00','2020-03-09 00:48:00');
