INSERT INTO `books` (`id`,`isbn`,`title`,`subtitle`,`author`,`publisher`,`published_date`,`description`,`page_count`,`category`,`image_link`,`read`,`createdAt`,`updatedAt`) VALUES (200,'9781592575008','The Complete Idiot\'s Guide to Psychology',NULL,'Joni E. Johnston','Alpha Books','2006','Interest in psychology extends well beyond the walls of academia. Sigmund Freud, Carl Jung, and William James are just a few examples of the many people in psychology whose works remain popular sellers, demonstrating an ongoing popular interest in this field. From students looking for a comprehensive resource to those looking for a self-help book, The Complete Idiot\'s Guide to Psychology, Third Edition, provides an easy-to-understand look at both the history and the modern practice of psychology.','434','Psychology','http://books.google.com/books/content?id=CbVtPwAACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api',0,'2020-03-10 02:23:20','2020-03-10 02:23:20');
