INSERT INTO `books` (`id`,`isbn`,`title`,`subtitle`,`author`,`publisher`,`published_date`,`description`,`page_count`,`category`,`image_link`,`read`,`createdAt`,`updatedAt`) VALUES (252,'9780996603003','Lady Mechanika Volume 1','Mystery of the Mechanical Corpse','Joe Benitez','BENITEX PROD','2015-12','The tabloid dubbed her \"Lady Mechanika\", the sole survivor of a mad scientist\'s horrific experiments which left her with mechanical limbs. Having no memory of her captivity or her former life, Lady Mechanika eventually built a new life for herself as an adventurer and private investigator, using her unique abilities to solve case the proper authorities couldn\'t or wouldn\'t handle. But she never stopped searching for the answers to her own past.','160','Private investigators','http://books.google.com/books/content?id=BWMojwEACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api',0,'2020-03-10 02:34:20','2020-03-10 02:34:20');
